package test.day0713;

public class Ex7For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//for문은 반복횟수가 정해져있는 경우에 많이 사용하는 반복문
		for(int i=1;i<=10;i++)
			System.out.print(i+" ");
		System.out.println();
		for(int i=10;i>=1;i--)
			System.out.print(i+" ");
		System.out.println();
		for(int i=1;i<=5;i++)
			System.out.print("*");
		System.out.println();
		for(int i=1;i<=5;i++)
			System.out.print("☆");
		System.out.println();
		for(int i=1;i<=5;i++)
			System.out.print("★");
		System.out.println();
	}

}
